import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Player from './components/Player';

const movies = [
  { id: 1, title: "Big Buck Bunny", url: "https://www.w3schools.com/html/mov_bbb.mp4", cover: "https://peach.blender.org/wp-content/uploads/title_anouncement.jpg?x11217" },
  { id: 2, title: "Sintel", url: "https://media.w3.org/2010/05/sintel/trailer.mp4", cover: "https://durian.blender.org/wp-content/uploads/2010/04/sintel_poster.jpg" },
  // Add more movies here
];

export default function App() {
  const [selectedMovie, setSelectedMovie] = useState(null);

  return (
    <Router>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/player">Player</Link></li>
        </ul>
      </nav>
      <Routes>
        <Route path="/" element={
          <div className="container">
            <h2>Movie Library</h2>
            <div className="movie-list">
              {movies.map((movie) => (
                <div className="movie-item" key={movie.id} onClick={() => setSelectedMovie(movie)}>
                  <img src={movie.cover} alt={movie.title} />
                  <h3>{movie.title}</h3>
                </div>
              ))}
            </div>
          </div>
        } />
        <Route path="/player" element={<Player movie={selectedMovie} />} />
      </Routes>
    </Router>
  );
}
